mapper.select:
SELECT * 
FROM stocks
WHERE corp_idx = #{corpIdx}
AND YEAR(stocked_at) = #{year}
AND MONTH(stocked_at) = #{month}

    AND prod_idx = #{prodIdx}

SELECT s.stock_idx AS stockIdx, c.corp_name AS company, p.prod_barcode AS productCode, p.prod_name AS productName, s.stock_cnt AS stockCnt, DATE_FORMAT(s.stocked_at, '%Y-%m-%d %H:%i:%s') AS date, '입고' AS type FROM stocks s JOIN corporates c ON s.corp_idx = c.corp_idx JOIN products p ON s.prod_idx = p.prod_idx
c.corp_name LIKE CONCAT('%', #{filterValue}, '%')
p.prod_barcode LIKE CONCAT('%', #{filterValue}, '%')
p.prod_name LIKE CONCAT('%', #{filterValue}, '%')
s.stock_cnt = #{filterValue}
DATE(s.stocked_at) = DATE(#{filterValue})
'입고' = #{filterValue}
ORDER BY s.stocked_at DESC LIMIT #{size} OFFSET #{offset}
